$( document ).ready(function() {
    $(".conditional").conditionize();

    $("#products").change(function () {
        $result = 0;
        for (var i=1; i<5; i++) {
            /*console.log($("#catevetements"+i+" option:selected").val());*/
            if($("#catevetements"+i+" option:selected").val() == 'Sac, pièce cuir, veste, manteau'){
                $result=$result+2;
            }
            else if($("#catevetements"+i+" option:selected").val() == 'Autre type de vêtement'){
                $result=$result+1;
            }
            else{
                $result=$result+0;
            }
            if($("#selectmarque"+i+" option:selected").val() == 'Luxe'){
                $result = $result+2;
            }
            else if($("#selectmarque"+i+" option:selected").val() == 'Semi-luxe'){
                $result = $result+1;
            }
        }
        if($result < 2){
            $('#1to4form')[0].action ='/index.php?controller=envoyeruncolis&fc=module&module=clientassupplier';
        }
        else{
            $('#1to4form')[0].action ='/index.php?controller=envoyerform&fc=module&module=clientassupplier';
        }
    });
});

